export default {
  props: ['quad', 'quadStyle', 'blink'],
  computed: {
    drawable: function drawable() {
      return this.quad.length === 4;
    },
    points: function points() {
      var q = this.quad;
      return "".concat(q[0].x, ",").concat(q[0].y, " ").concat(q[1].x, ",").concat(q[1].y, " ").concat(q[2].x, ",").concat(q[2].y, " ").concat(q[3].x, ",").concat(q[3].y);
    },
    defaultStyle: function defaultStyle() {
      var defaultStyle = "stroke:purple;stroke-width:1;stroke-opacity:0.99;fill-opacity:0.5;";

      if (this.$store.state.annotation.boxingMode === 'letters') {
        return defaultStyle.concat("fill:lime;");
      } else {
        return defaultStyle.concat("fill:blue;");
      }
    }
  },
  data: function data() {
    return {
      polygonId: Math.random().toString(36).substr(2, 11),
      circleId: Math.random().toString(36).substr(2, 11)
    };
  }
};
